interface JQuery {

    dropdown(): JQuery;
    dropdown(args: any): JQuery;
    dropdown(arg: string, opts: any): JQuery;

    checkbox(): JQuery;
    checkbox(args: any): JQuery;
    checkbox(arg: string, opts: any): JQuery;

    modal(): JQuery;
    modal(args: any): JQuery;
    modal(arg: string, opts: any): JQuery;

}